import { useState } from "react"

function App() {

  const [showText , setShowText] = useState(false);

  const handleToggele = () => {
    setShowText(showText)
  }
  return(
    <div style={{ textAlign : 'center',marginTop : '50px'}}>
        <button onClick={handleToggele}>{showText ? 'نمایش بده ':'پنهان کن '}</button>
        {showText && <p style={{marginTop: '20px'}}>این یک متن مخفی است</p>}
    </div>
  )
}
export default App
