import{ useState } from "react";
import './Counter.css';
const Counter = () => {
    const [count , setCount] = useState(0)

    const increaseHandler = () =>{
        setCount(count + 1)
    }
    const decreaseHandler = () => {
        setCount(count - 1)
    }
    const reserHamdler = () => {
        setCount(0)
    }
    return(
        <>
        <h2>{count}</h2>
        {count > 0 && (
            <button onClick={decreaseHandler} className='decrease'>
            decrease
            </button>
        )}
        <button onClick={increaseHandler} className='increase'>increase</button>
        <button onClick={reserHamdler} className='reser'>
            reset</button>
        </>
    );
};
export default Counter
