import { useState } from 'react'
import './Message.css'
function Message(){

    let[myRoom , setMyRoom] = useState('success')
    const clickHandler = ()=>{
        myRoom == 'success' ? setMyRoom('failed') : setMyRoom('success')
    }

    return(
        <>
        <div className={`messageBox ${myRoom}`}>
            {
                myRoom == 'success' ? 'l,' : "پرداخت با موفقیت انجام نشد"
            }
        </div>
        <button onClick={clickHandler}> click me </button>
        </>
    )
}

export default Message
