import React, { useState } from "react";

const Counter = () => {

    const [counter, SetCounter] = useState(0);
    const increaseCounter = () => {
        SetCounter(counter + 1);
    }
    const decreaseCounter = () => {
        SetCounter(counter - 1)
    }

    const [array, setArray] = useState(["amir", "mamad", "hossein"]);
    const handelAddNewPlayer =()=>{
        setArray ([...array,"ali","reza","hasan"])
    }
    return <div>
        <ul> {array.map((item, index) => {
            return<li >{item}</li>
            })}
         </ul>
        <button onClick={handelAddNewPlayer}>add new player</button>
        <button onClick={decreaseCounter}>-</button>
        <span>{counter}</span>
        <button onClick={increaseCounter}>+</button>
    </div>
}
export default Counter  // Big Name
