import { useEffect, useState } from "react";

function kir(){
    const [countr , setCountr] = useState(0);
    useEffect( () => {
        document.title = `شما ${countr} بار کلیک کردید`
    } , [countr])

    return(
        <div className="p-4">
        <button onClick={ ()=> setCountr(countr + 1)}></button>
    </div>
)

};
export default kir
