import { useState } from 'react'
import './Message.css'
function Message(){

    let[myRoom , setMyRoom] = useState('success')
    const clickHandler = ()=>{
        myRoom == 'success' ? setMyRoom('faild') : setMyRoom
    }

    return(
        <>
        <div className={`messageBox ${myRoom}`}>
            پرداخت با موفقیت انجام شد
        </div>
        <button onClick={clickHandler}> click me </button>
        </>
    )
}

export default Message
