import { useEffect, useState } from "react";

function Kir(){
   const [countr , setCountr] = useState(0);
    useEffect( () => {
        document.title = `شما ${countr} بار کلیک کردید`
    } )

    return(
        <div className="p-4">
        <button onClick={ ()=> setCountr(countr + 1)}>click me</button>
    </div>
)

};
export default Kir
