function App(){
    const eventHandler = (e)=>{
        let select = [e.target.selectOptions];
        select = select.map( item => item.value) 
        console.log(select)
    }
    
    return(
        <form>
            <select onChange={eventHandler} multiple>
                <option value="arak">arak</option>
                <option value="shiraz">shiraz</option>
                <option value="zahedan">zahedan</option>
                <option value="birjand">birjand</option>
            </select>
        </form>
    )
}
export default App
